import React from "react";
import { FaGoogle } from "react-icons/fa";

const Login = () => {
  // Function to trigger login redirect
  const handleGoogleLogin = () => {
    window.open("http://localhost:5000/auth/google", "_self");
  };

  return (
    <div className="h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 to-purple-600">
      <div className="bg-white p-10 rounded-2xl shadow-xl max-w-md w-full text-center">
        <h1 className="text-3xl font-bold text-gray-800 mb-4">
          Real-Time Doc Editor
        </h1>
        <p className="text-gray-500 mb-6">Login to collaborate in real-time</p>

        <button
          onClick={handleGoogleLogin}
          className="flex items-center justify-center gap-3 bg-red-500 text-white py-3 px-6 rounded-xl hover:bg-red-600 transition-all font-semibold w-full"
        >
          <FaGoogle className="text-xl" />
          Sign in with Google
        </button>
      </div>
    </div>
  );
};

export default Login;
