import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { v4 as uuidV4 } from 'uuid';
import TextEditor from './components/TextEditor';
import Login from './pages/Login';
import Editor from './pages/Editor'; // optional
import axios from 'axios';

const App = () => {
  const [user, setUser] = useState(null); // Store user state
  const [loading, setLoading] = useState(true); // Loading status

  useEffect(() => {
    // Fetch logged-in user from backend
    const fetchUser = async () => {
      try {
        const res = await axios.get('http://localhost:5000/auth/me', {
          withCredentials: true,
        });
        setUser(res.data);
      } catch (err) {
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) return <div className="h-screen flex justify-center items-center text-xl">Loading...</div>;

  return (
    <Router>
      <Routes>
        {/* Public Route */}
        <Route path="/" element={user ? <Navigate to={`/docs/${uuidV4()}`} /> : <Login />} />

        {/* Protected Route */}
        <Route
          path="/docs/:id"
          element={user ? <TextEditor user={user} /> : <Navigate to="/" />}
        />

        {/* Optional editor page */}
        <Route path="/editor" element={user ? <Editor /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
